function testUser () {
    if (termsList.length > 0) {
        let currentlyStudyingDefinitions: string[] = []
        tempStudyingDefinitions = []
        tempStudyingTerms = []
        studying = true
        currentlyStudyingTerms = []
        correctGuesses = 0
        for (let value of termsList) {
            currentlyStudyingTerms.push(convertToText(value))
        }
        for (let value2 of definitionsList) {
            currentlyStudyingDefinitions.push(convertToText(value2))
        }
        numberOfTerms = currentlyStudyingTerms.length
        while (studying) {
            if (currentlyStudyingTerms.length > 0) {
                game.showLongText(currentlyStudyingTerms[0], DialogLayout.Center)
                game.showLongText(currentlyStudyingDefinitions[0], DialogLayout.Center)
                studyingNumber = 0
                if (!(game.ask("Did you get the term correct? A - Yes, B - No"))) {
                    tempStudyingDefinitions.push(currentlyStudyingDefinitions[0])
                    tempStudyingTerms.push(currentlyStudyingTerms[0])
                } else {
                    correctGuesses += 1
                }
                currentlyStudyingTerms.removeAt(0)
                currentlyStudyingDefinitions.removeAt(0)
            } else if (tempStudyingTerms.length > 0) {
                game.showLongText("You got " + correctGuesses + " definitions correct and " + (numberOfTerms - correctGuesses) + " wrong.", DialogLayout.Center)
                game.showLongText("You finished studying all the terms. Study the ones you got wrong?", DialogLayout.Center)
                if (game.ask("A to continue studying, B to exit.")) {
                    for (let value3 of tempStudyingTerms) {
                        currentlyStudyingTerms.push(convertToText(value3))
                    }
                    for (let value4 of tempStudyingDefinitions) {
                        currentlyStudyingDefinitions.push(convertToText(value4))
                    }
                    tempStudyingTerms = []
                    tempStudyingDefinitions = []
                    numberOfTerms = currentlyStudyingTerms.length
                    correctGuesses = 0
                } else {
                    studying = false
                }
            } else {
                studying = false
            }
        }
        game.showLongText("You have finished studying. You got" + correctGuesses + " definitions correct and " + (numberOfTerms - correctGuesses) + " wrong.", DialogLayout.Center)
    } else {
        game.showLongText("There are no terms to study. Select \"N\" to add terms.", DialogLayout.Bottom)
    }
}
